  components: {
    Timeline,
    CalComp,
    Search,
    Filters
  },
  methods: {
    getEventTypes() {
      if (this.custom.types) {
        return this.custom.types;
      } else {
        return false
      }
    },
    getEvents(tag) {
      axios.get(
        `${this.baseUrl}/webkit_components/topics.json?serializer=event&tags=${tag}&per=500`
      ).then(({ data }) => {
        this.data = data
          .filter(({ event }) => event)
          .map(event => ({
            ...event,
            date: this.formatDate(event.event.start),
            class: "marked"
          }))
          .sort((a, b) => a.event.start.localeCompare(b.event.start));
        this.selectEvent(this.dataReverse[0]);
      });
    },
    formatDate(value) {
      return moment(value).format("D-M-YYYY");
    },
    formatTime(value) {
      return moment(value).format("HH:mm");
    }
  },
  created() {
    this.getEvents(this.custom.tag);
  },
  computed: {
    dataReverse: function() {
      return this.data.slice().reverse();
    }
  },
  mounted() {
    if (this.$mq == "md") {
      this.minimize = false;
    }
  },
  filters: {
    formatDate: function(value) {
      return moment(value).format("MMMM Do YYYY");
    },
    formatMonth: function(value) {
      return moment(value).format("MMMM YYYY");
    }
  }
};
